sys_ui_action.script:
function loadDraft() {
	var tableName = g_form.getTableName();
	var draftNamesOffline = FormDraftClient.getLocalDraftNames(tableName);

	var ga = new GlideAjax('FormDraftAjax');
	ga.addParam('sysparm_name', 'loadDraftNames');
	ga.addParam('sysparm_tablename', tableName);
	ga.getXMLAnswer(function(draftNames) {

		var gDialog = new GlideDialogWindow('draft_select_prompt');
		gDialog.setTitle(getMessage("Select a draft to load: "));
		gDialog.setPreference('draftNames', draftNames);
		gDialog.setPreference('draftNamesOffline', draftNamesOffline);
		gDialog.setPreference('onPromptComplete', complete);
		gDialog.render();
	});

	function complete(setID, offline) {
		if(!offline) {
			var ga = new GlideAjax('FormDraftAjax');
			ga.addParam('sysparm_name', 'getDraftData');
			ga.addParam('sysparm_tablename', tableName);
			ga.addParam('sysparm_recordname', setID);
			ga.getXMLAnswer(function(response) {
				response = JSON.parse(response);
				for (var key in response) {
					g_form.setValue(key, response[key]);
					g_form.addDecoration(key, "icon-success", "Modified by '" + setID + "' draft");
				}
			});
			g_form.addInfoMessage("Draft: '" + setID + "' is loaded into form and deleted.");
		}
		else {
			var draftData = FormDraftClient.getLocalData(setID);
			for (var key in draftData) {
				g_form.setValue(key, draftData[key]);
				g_form.addDecoration(key, "icon-success", "Modified by '" + setID + "' draft");
			}
			g_form.addInfoMessage("Draft: '" + setID + "' is loaded into form and deleted from local storage.");
		}
	}

	function deleteDraft(selectItem) {
		var msg = "Delete " + selectItem + " draft record?";
			g_modal.confirmDestroy("Confirmation", msg, function (ret) {
				alert(ret);
			});
	}
}
