sys_ui_script.script:
var FormDraftClient = (function() {
	return {
		setLocalData: setLocalData,
		getLocalData: getLocalData,
		getLocalDraftNames: getLocalDraftNames
	};

	function setLocalData(tablename, key, value) {

		window.localStorage.setItem(key, value);

		tableDrafts = window.localStorage.getItem(tablename);
		if(tableDrafts == null) {
			tableDrafts = [key];
		}
		else {
			tableDrafts = tableDrafts.split(",");
			tableDrafts.push(key);
		}
		window.localStorage.setItem(tablename, tableDrafts);
	}

	function getLocalData(key) {
		return JSON.parse(window.localStorage.getItem(key));
	}

	function getLocalDraftNames(tablename) {
		return window.localStorage.getItem(tablename);
	}
})();
